var smartTit = -1;
var smartCont = -1;
var outData;
var config = {};
var cEls = {};
var sBtns = {};
var clusterEls = [];
var greenDivColor = "rgb(192, 251, 191)";
var yellowDivColor = "rgb(250, 251, 191)";
var redDivColor = "rgb(252, 219, 219)";

var sourceURL;
var URL;
var lastSlash;
var urlDir;


function completeHandler(data){
	outData = data;
	var contentEl = document.getElementById("parsedContent");
	contentEl.innerHTML = "";

	var pOut = document.createElement("div");
	pOut.className = "pOut-container";
	contentEl.appendChild(pOut);

	var jClusters = data.clusters;
	smartCont = data.contSel;
	smartTit = data.titSel;

	var clusters = [];
	for(var i=0; i<jClusters.length; i++){
		clusters.push(new Cluster(jClusters[i],pOut));
	}
	genSizeConfigs(data.featMap);
	genConfigTags(data.configs);


}
function errorHandler(res){
	console.log("FAILED TO FETCH JSON");
}

function Cluster(cluster,parentEl) {
  this.size = cluster.size;
  this.el = document.createElement("div");
  this.el.style.fontSize = (this.size*4)+"px";
  this.el.className = "cluster";
  this.el.innerHTML = cluster.content;
  parentEl.appendChild(this.el);

   if(this.size === smartCont){
  	this.el.style.backgroundColor = greenDivColor;
  }else if(this.size === smartTit){
  	this.el.style.backgroundColor = yellowDivColor;
  }else{
  	this.el.style.backgroundColor = redDivColor;
  }
  addEls(this.size,this.el);
  var myEl = this.el;
  var mySize = this.size;
  this.el.addEventListener("click",function(){toggleType(myEl,mySize)});
}


function addEls(size,el){
	if(cEls[size] == null){
		cEls[size] = [el];
	}else{
		cEls[size].push(el);
	}
	clusterEls.push(el);
}

function genSizeConfigs(featMap){
	var myConfig = document.getElementById("myConfig");

	var optionDiv = document.createElement("div");
	optionDiv.className = "option-div";

	var sizes = [];
	for (var key in featMap) {
	  sizes.push(key);
	}
	//console.log(sizes);
	for(var i=0; i<sizes.length; i++){
		optionDiv.appendChild(getSizeConfigDiv(sizes[i]));
	}
	myConfig.appendChild(optionDiv);


	var publishConfigText = document.createElement("p");
	//<h4 class='font-sizes-text'> Edit Configuration </h4>
	publishConfigText.className = "publish-config-text";
	publishConfigText.innerHTML = "Publish Your Configuration";
	myConfig.appendChild(publishConfigText);

	var urlInfoText = document.createElement("p");
	urlInfoText.innerHTML = "The configuration will be applied to all pages in the following url directory";
	myConfig.appendChild(urlInfoText);

	var inputUrlDir = document.createElement("input");
	inputUrlDir.className = "url-dir";
	inputUrlDir.value = urlDir;
	myConfig.appendChild(inputUrlDir);

	var configNameText = document.createElement("p");
	configNameText.className="config-name-text";
	configNameText.innerHTML = "Name your configuration";
	myConfig.appendChild(configNameText);

	var inputName = document.createElement("input");
	inputName.className = "url-name";
	inputName.placeholder="BestConfig"
	myConfig.appendChild(inputName);

	var publishBtn = document.createElement("button");
	publishBtn.className = "publish-button";
	publishBtn.innerHTML = "Publish Configuration";
	publishBtn.addEventListener("click",function(){ sendConfig(inputUrlDir.value,inputName.value)});
	myConfig.appendChild(publishBtn);
	
}



function getSizeConfigDiv(size){
	var sizeConfig = document.createElement("div");
	sizeConfig.className = "size-config";

	var isTit = smartTit==size;
	var isCont = smartCont==size;
	var ignore = !isTit && !isCont;

	if(isTit)
		config[size] = "title";
	if(isCont)
		config[size] = "content";
	if(ignore)
		config[size] = "ignore";

	var titBtn = getConfigBtn("yellow",isTit);
	var contBtn = getConfigBtn("green",isCont);
	var ignoreBtn = getConfigBtn("red",ignore);
	var sibs = [titBtn,contBtn,ignoreBtn];
	titBtn.addEventListener("click",function(){toggleMe(sibs,size,0)});
	contBtn.addEventListener("click",function(){toggleMe(sibs,size,1)});
	ignoreBtn.addEventListener("click",function(){toggleMe(sibs,size,2)});

	sBtns[size] = sibs;

	var sizeText = document.createElement("h4");
	sizeText.className = "config-text";
	sizeText.innerHTML = size;

	sizeConfig.appendChild(sizeText);
	sizeConfig.appendChild(titBtn);
	sizeConfig.appendChild(contBtn);
	sizeConfig.appendChild(ignoreBtn);

	return sizeConfig;
}

function toggleMe(sibs,size,type){
	var el = sibs[type];
	for(var i=0; i<sibs.length;i++){
		sibs[i].children[1].style.opacity=0;
	}
	el.children[1].style.opacity=1;
	if(type == 0){
		reColorFamily(size,yellowDivColor);
	}else if(type == 1){
		reColorFamily(size,greenDivColor);
	}else{
		reColorFamily(size,redDivColor);
	}

	if(bumpEl !=null){
		bumpEl.style.opacity = .65;
		clearConfigEls();
	}
}

function toggleType(clustEl,size){
	var order = [yellowDivColor,greenDivColor,redDivColor];
	var elColor = clustEl.style.backgroundColor;
	var cIndex = order.indexOf(elColor);
	var nextColor = order[(++cIndex)%order.length];
	if(nextColor == yellowDivColor){
		toggleMe(sBtns[size],size,0);
	}else if(nextColor == greenDivColor){
		toggleMe(sBtns[size],size,1);
	}else{
		toggleMe(sBtns[size],size,2);
	}
}


function reColorFamily(size,color){
	if(cEls[size]!=null){
		for(var i=0; i<cEls[size].length; i++){
			cEls[size][i].style.backgroundColor = color;
		}
	}
	if(color === yellowDivColor){
		 config[size] = "title";
	}else if(color === greenDivColor){
		config[size] = "content";
	}else{
		config[size] = "ignore";
	}
}

function getConfigBtn(color,show){
	var confBtn = document.createElement("div");
	var hidden = show?"":"hidden";
	confBtn.className = "side-by-side";
	confBtn.innerHTML = "<div class='check-btn "+color+"-box side-"+color+"'></div><div id='check' class='checkmark "+hidden+" "+color+"-check side2-"+color+"'>✓</div>";
	return confBtn;
}


function toggleHideIgnores(el){
	if(el.children[2].style.opacity==0){
		el.children[2].style.opacity=1;
		for(var i=0; i<clusterEls.length; i++){
			if(clusterEls[i].style.backgroundColor == redDivColor){
				clusterEls[i].style.display = "none";
			}
		}
	}else{
		el.children[2].style.opacity=0;
		for(var i=0; i<clusterEls.length; i++){
			if(clusterEls[i].style.backgroundColor == redDivColor){
				clusterEls[i].style.display = "inherit";
			}
		}
	}
}

function sendConfig(urlDIR,username){
	if(urlDIR.length==0){
		alert("Please enter a url directory");
		return;
	}
	if(username.length==0){
		alert("Please enter a configuration name");
		return;
	}
	var confStr = JSON.stringify(config);
	$.post( "http://108.167.189.29/~saternius/WebScraper/submitConfig.php", { "urlDir": urlDIR, "name": username, "config":confStr })
  	.done(function( data ) {
    	alert("Configuration Uploaded. Thank you for your contribution!");
    	location.reload(); 
  	});

	function errorHandler(){
		alert("Error submiting your configuration.");
	}
}

var tagEls = [];
var bumpEl;
var focusConfigEl;
function genConfigTags(configs){
	if(configs.length>0){
		var container = document.getElementById("otherConfigs");
		container.innerHTML = "Feel free to select some of the configurations made by other users.<br><br>";
		for(var i=0; i<configs.length;i++){
			var tag = document.createElement("div");
			tag.className = "semGrade ticker-tag";
			tag.innerHTML = configs[i].name;
			container.appendChild(tag);
			var config = configs[i].config;
			(function(tag,config){
				tag.addEventListener("click",function(){
						loadConfig(tag,config);
				});
			})(tag,config)
			tagEls.push(tag);

			if(i==0){
				tag.style.opacity = 1;
				reColor(config)
			}
		}

		var bump = document.createElement("div");
		bump.className = "semGrade ticker-tag greenTick";
		bump.innerHTML = "BUMP THIS CONFIG";
		bump.style.marginTop = "15px";
		bump.style.padding = "10px";
		bumpEl = bump;
		bump.addEventListener("click",bumpConfig);
		container.appendChild(bump);
	}
}

function bumpConfig(){
	if(bumpEl.style.opacity == 1){
		
		console.log(urlDir+":"+focusConfigEl.innerHTML);
		$.post( "http://108.167.189.29/~saternius/WebScraper/bumpConfig.php", { "urlDir": urlDir, "confName":focusConfigEl.innerHTML })
	  	.done(function( data ) {
	    	alert("Configuration Bumped");
	    	location.reload(); 
	  	});

		function errorHandler(){
			alert("Failed to bump.");
		}

	}
}

function reColor(config){
	for (var key in config) {
	   var val = config[key];
	   if(val==="title"){
	   		reColorFamily(key,yellowDivColor);
	   }else if(val === "content"){
	   		reColorFamily(key,greenDivColor);
	   }else{
	   		reColorFamily(key,redDivColor);
	   }
	}
}
function loadConfig(el,config){
	reColor(config);
	clearConfigEls();
	el.style.opacity = 1;
	bumpEl.style.opacity = 1;
	focusConfigEl = el;

}

function clearConfigEls(){
	for(var i=0; i<tagEls.length; i++){
		tagEls[i].style.opacity = .65;
	}
}

